public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello Java!");
        System.out.println("Welcome to Java!");
    }
}

int age = 20;
double pi = 3.14;
boolean isActive = true;
char grade = 'A';
String name = "Alice";
System.out.println("Age: " + age);
System.out.println("Pi: " + pi);
System.out.println("Active: " + isActive);
System.out.println("Grade: " + grade);
System.out.println("Name: " + name);

int age = 18;
if (age >= 18) {
    System.out.println("Bạn đã trưởng thành");
} else {
    System.out.println("Bạn chưa trưởng thành");
}

int day = 3;
switch (day) {
    case 1:
        System.out.println("Thứ Hai");
        break;
    case 2:
        System.out.println("Thứ Ba");
        break;
    case 3:
        System.out.println("Thứ Tư");
        break;
    default:
        System.out.println("Không xác định");
        break;
}

for(int i = 0; i < 5; i++) {
    System.out.println("i = " + i);
}

int count = 1;
while(count <= 3) {
    System.out.println("Count: " + count);
    count++;
}

public int sum(int a, int b) {
    return a + b;
}

int result = sum(5, 10);
System.out.println("Tổng: " + result);

// Hàm với nhiều tham số
public int multiply(int x, int y, int z) {
    return x * y * z;
}
System.out.println("Tích: " + multiply(2,3,4));

int[] numbers = {1, 2, 3, 4, 5};
for(int n : numbers) {
    System.out.println(n);
}

String name = "John Doe";
System.out.println("Hello, " + name);
System.out.println("Tên dài: " + name.length());
System.out.println("Ký tự đầu: " + name.charAt(0));

class Person {
    String name;
    int age;

    void sayHello() {
        System.out.println("Hello, " + name);
    }
}

Person p = new Person();
p.name = "Alice";
p.age = 25;
p.sayHello();

